import React, { useState } from 'react'
import Title from '../components/Title'
import { Dados } from '../components/Dados'


export const Cadastro = () => {
  const [free, setFree] = useState(true)
  const [plus, setPlus] = useState(false)

  const submitForm = (event) => {
    event.preventDefault()
    console.log(formValues)


  }

  const [formValues, setFormValues] = useState({
    nome: '',
    telefone: '',
    endereco: '',
    dadoscartao: '',
    cardnumber: '',
    cvc: '',
    plano: 'free'
  });

  const handleInput = (event) => {
    const { name, value } = event.target;
    setFormValues({ ...formValues, [name]: value })
  }

  const clickFree = () => {
    setFree(true)
    setPlus(false)

    setFormValues({
      ...formValues,
      plano: 'free'
    })
  }

  const clickPlus = () => {
    setFree(false)
    setPlus(true)

    setFormValues({
      ...formValues,
      plano: 'plus'
    })
  }

  const validateCardNumber = (cardNumber) => {
    const regex = /^[0-9]{20}$/;
    return regex.test(cardNumber);
}

  function handleClick() {
    alert("Plano assinado com sucesso!")
  }

  return (
    <div className='container text-center'>
      <Title title={"Cadastro de Assinante"} text="" />
      <Dados />
      <form className='p-3' onSubmit={submitForm}>
        <div className='row'>
          <div className='col'>
            Dados Pessoais:
            <br />
            <input required type="text" placeholder='Nome' name='nome' value={formValues.nome} onChange={handleInput} />
            <br />
            <input required type="number" placeholder='Telefone' name='telefone' value={formValues.telefone} onChange={handleInput} />
            <br />
            <input required type="text" placeholder='Endereço' name='endereco' value={formValues.endereco} onChange={handleInput} />
            <br /><br />
            Plano Escolhido:
            <div className="d-flex justify-content-center planos">
              <button className='m-2' type='button' onClick={clickFree} style={{ backgroundColor: free ? '#0D6EFD' : 'white' }}>FREE</button>
              <br />
              <button className='m-2' type='button' onClick={clickPlus} style={{ backgroundColor: plus ? '#0D6EFD' : 'white' }}>PLUS</button>
            </div>
          </div>

          <div className='col'>
            Dados do Cartão:
            <br />
            <input required type="text" placeholder='Nome do cartão' name='dadoscartao' value={formValues.dadoscartao} onChange={handleInput} />
            <br />
            <input required type="number" maxLength="20" value={cardnumber} placeholder='Número do cartão' name='numerocartao' value={formValues.numerocartao} onChange={handleInput}  />
            <br />
            <input required type="password" maxLength="3" pattern="[0-9]{3}" placeholder='CVC' name='cvc' value={formValues.cvc} onChange={handleInput} />
          </div>
        </div>
        <button type='submit' className='w-25' onClick={handleClick}>ASSINAR</button>
      </form>

    </div>

  )
}
