            <div id={id} className="rectang">
              {FREE}
            </div>
          </button>

          <button>PLUS</button>
        </div>

        <div className='col'>
          Dados do Cartão
          <br />
          <input type="text" placeholder='Nome do cartão'name='dadoscartao' />
          <br />
          <input type="number" maxLength="20" pattern="[0-9]{20}" placeholder='Número do cartão' name='numerocartao' />
          <br />
          <input type="password" maxLength="3" pattern="[0-9]{3}" placeholder='CVC' name='cvc' />
        </div>
      </form>
      <div>
        <button>ASSINAR</button>
