import React, { useState } from 'react'
import Title from '../components/Title'
import { Dados } from '../components/Dados'


export const Cadastro = () => {

  const submitForm = (event) => {
    event.preventDefault()
    console.log(formValues)
  }

  const [formValues, setFormValues] = useState({
    nome: '',
    telefone: '',
    endereco: '',
    dadoscartao: '',
    numerocartao: '',
    cvc: ''
  });

  const handleInput = (event) => {
    const { name, value } = event.target;
    setFormValues({ ...formValues, [name]: value })
  }

  function click( id, color) {
    let element = document.getElementById(id);
    if (element != null) {
      element.style.backgroundColor = active ? color : "#C4C4C4";
    }
  }

  return (
    <div className='container text-center'>
      <Title title={"Cadastro de Assinante"} text="" />
      <Dados />
      <form className='row p-3' onSubmit={submitForm}>
        <div className='col'>
          Dados Pessoais
          <br />
          <input type="text" placeholder='Nome' name='nome' value={formValues.nome} onChange={handleInput} />
          <br />
          <input type="number" placeholder='Telefone' name='telefone' value={formValues.telefone} onChange={handleInput} />
          <br />
          <input type="text" placeholder='Endereço' name='endereco' value={formValues.endereco} onChange={handleInput} />
          <br /><br />
          Plano Escolhido
          <br />
          <button>PLUS</button>
          <button>PLUS</button>
        </div>

        <div className='col'>
          Dados do Cartão
          <br />
          <input type="text" placeholder='Nome do cartão' name='dadoscartao' value={formValues.dadoscartao} onChange={handleInput} />
          <br />
          <input type="number" maxLength="20" pattern="[0-9]{20}" placeholder='Número do cartão' name='numerocartao' value={formValues.numerocartao} onChange={handleInput} />
          <br />
          <input type="password" maxLength="3" pattern="[0-9]{3}" placeholder='CVC' name='cvc' value={formValues.cvc} onChange={handleInput} />
        </div>
        <br />
        <button type='submit'>ASSINAR</button>
      </form>

    </div>

  )
}
